<template>
    <div :class="['alert', `alert-${type}`]" role="alert">
        <button v-if="dismissable" @click="onClose" type="button" class="close" data-dismiss="alert" aria-label="Close">
            <span aria-hidden="true">&times;</span>
        </button>
        <slot></slot>
    </div>
</template>

<script>
    export default {
        props: {
            type: {
                type: String,
                default: 'info'
            },
            dismissable: {
                type: Boolean,
                default: false
            }
        },
        methods: {
            onClose: function() {
                this.$emit('close');
            }
        }
    }
</script>