<template>
    <ol class="breadcrumb">
        <li v-for="(item, index) in data"  
            @click="item.click" 
            :class="['breadcrumb-item', (index === data.length - 1) ? 'active' : '']">
            <a v-if="index !== data.length - 1" :href="item.href">{{item.content}}</a>
            <a v-else>{{item.content}}</a>
        </li>
    </ol>
</template>

<script>
    export default {
        props: {
            data: {
                type: Array,
                required: true
            }
        }
    }
</script>