<template>
    <a role="button" :class="['btn', outline ? `btn-outline-${type}` : `btn-${type}`, buttonSize, 'active']" :href="href" @click="$emit('click')"><slot></slot></a>
</template>

<script>
    export default {
        props: {
            type: {
                type: String,
                default: 'primary'
            },
            outline: {
                type: Boolean,
                default: false
            },
            size: {
                type: String,
                default: ''
            },
            href: {
                type: String,
                default: 'javascript:;'
            }
        },
        computed: {
            buttonSize: function() {
                if(this.size === 'large') {
                    return 'btn-lg';
                } else if(this.size === 'small') {
                    return 'btn-sm';
                } else {
                    return '';
                }
            }
        }
    }
</script>