<template>
    <div :class="[(layout === 'vertical') ? 'btn-group-vertical' : 'btn-group', buttonGroupSize]" role="group">
        <slot></slot>
    </div>
</template>

<script>
    export default {
        props: {
            layout: {
                type: String,
                default: 'horizontal'
            },
            size: {
                type: String,
                default: 'normal'
            }
        },
        computed: {
            buttonGroupSize: function() {
                if(this.size === 'large') {
                    return 'btn-group-lg';
                } else if (this.size === 'small') {
                    return 'btn-group-sm';
                } else {
                    return '';
                }
            }
        }
    }
</script>