<template>
    <div :class="[
        'col', 
        (xs != null)?`col-${xs}`:'', 
        (sm != null)?`col-sm-${sm}`:'', 
        (md != null)?`col-md-${md}`:'', 
        (lg != null)?`col-lg-${lg}`:'', 
        (xl != null)?`col-xl-${xl}`:'', 
        `align-self-${vertical}`, 
        `flex-${order}`,
        (xsOffset != null)?`offset-${xsOffset}`:'',
        (smOffset != null)?`offset-sm-${smOffset}`:'',
        (mdOffset != null)?`offset-md-${mdOffset}`:'',
        (lgOffset != null)?`offset-lg-${lgOffset}`:'',
        (xlOffset)?`offset-xl-${xlOffset}`:'']">
        <slot></slot>
    </div>
</template>

<script>
    export default {
        props: {
            xs: {
                type: Number
            },
            sm: {
                type: Number
            },
            md: {
                type: Number
            },
            lg: {
                type: Number
            },
            xl: {
                type: Number
            },
            vertical: {
                type: String,
                default: 'start'
            },
            order: {
                type: String,
                default: 'first'
            },
            xsOffset: {
                type: Number
            },
            smOffset: {
                type: Number
            },
            mdOffset: {
                type: Number
            },
            lgOffset: {
                type: Number
            },
            xlOffset: {
                type: Number
            },
        }
    }
</script>