<template>
    <div :class="['btn-group', `drop${direction}`, menuDisplay ? 'show' : '']">
        <vButton :class="[split ? '' : 'dropdown-toggle']" @click="titleClick">{{title}}</vButton>
        <vButton v-if="split" class="dropdown-toggle dropdown-toggle-split" @click="toggleClick"><span class="sr-only">Toggle Dropdown</span></vButton>
        <slot></slot>
    </div>
</template>

<script>
    import vButton from './Button.vue';
    export default {
        props: {
            direction: {
                type: String,
                default: 'down'
            },
            menuAlign: {
                type: String,
                default: 'default'
            },
            size: {
                type: String,
                default: 'default'
            },
            split: {
                type: Boolean,
                default: false
            },
            title: {
                type: String,
                required: true
            }
        },
        data: function() {
            return {
                menuDisplay: false
            }
        },
        components: {
            vButton
        },
        methods: {
            titleClick: function() {
                if(!this.split) {
                    this.menuDisplay = !this.menuDisplay;
                }
            },
            toggleClick: function() {
                this.menuDisplay = !this.menuDisplay;
            }
        }
    }
</script>