<template>
    <a v-if="action" :href="href" :class="['list-group-item', 'list-group-item-action', disabled ? 'disabled' : '', active ? 'active' : '', (type !== 'default') ? `list-group-item-${type}` : '']"><slot></slot></a>
</template>

<script>
    export default {
        props: {
            type: {
                type: String,
                default: 'default'
            },
            action: {
                type: Boolean,
                default: true
            },
            href: {
                type: String,
                default: 'javascript:;'
            },
            disabled: {
                type: Boolean,
                default: false
            },
            active: {
                type: Boolean,
                default: false
            }
        }
    }
</script>