<template>
    <transition-group
        @after-enter="enter"
        @leave="leave">
        <div key="el" v-show="visible" :class="['modal', 'fade']" tabindex="-1" role="dialog">
            <div class="modal-dialog" role="document">
                <div class="modal-content">
                    <div v-if="header != null" class="modal-header">
                        <h5 class="modal-title">{{header}}</h5>
                        <button type="button" class="close" data-dismiss="modal" aria-label="Close" @click="$emit('closeButtonClick')">
                            <span :aria-hidden="!visible">&times;</span>
                        </button>
                    </div>
                    <div class="modal-body">
                        <slot name="content"></slot>
                    </div>
                    <div class="modal-footer">
                        <slot name="footer"></slot>
                    </div>
                </div>
            </div>
        </div>
        <div key="backdrop" v-if="visible" class="modal-backdrop fade show"></div>
    </transition-group>
</template>

<script>
    export default {
        props: {
            header: {
                type: String
            },
            visible: {
                type: Boolean,
                default: false
            }
        },
        data: function () {
            return {
                styleObject: {
                    display: 'none'
                }
            };
        },
        methods: {
            enter: function(el) {
                document.body.classList.add('modal-open');
                el.classList.add('show');
                this.$emit('open');
            },
            leave: function(el) {
                document.body.classList.remove('modal-open');
                el.classList.remove('show');
                this.$emit('close');
            },
            backdropClick: function() {
                console.log('click');
            }
        }
    }
</script>

<style>
.modal {
    display: block;
}
.modal-open {
    padding-right: 15px;
}
</style>