<template>
    <div 
        :class="['progress-bar', (background !== 'default') ? `bg-${background}` : '', strip ? 'progress-bar-striped' : '', animate ? 'progress-bar-animated' : '']"
        role="progressbar"
        :style="[width]" >
        <slot></slot>
    </div>
</template>

<script>
    export default {
        props: {
            percent: {
                type: Number,
                required: true
            },
            background: {
                type: String,
                default: 'default'
            },
            strip: {
                type: Boolean,
                default: false
            },
            animate: {
                type: Boolean,
                default: false
            }
        },
        computed: {
            width: function() {
                return {
                    width: `${this.percent}%`
                };
            }
        }
    }
</script>